INSERT INTO Albums (Name, ReleaseDate, CoverImageUrl, Description, DateCreated) VALUES('She''s So Unusual', '1983-10-14', 'https://tinyurl.com/bdzhs2fr', 'She''s So Unusual is the debut studio album by American singer and songwriter Cyndi Lauper, released on October 14, 1983, by Portrait Records. The album was re-released in 2014 to commemorate its 30th anniversary, and was called She''s So Unusual: A 30th Anniversary Celebration. The re-release contains demos and remixes of previously released material, as well as new artwork.', DATE('now'));
INSERT INTO Albums (Name, ReleaseDate, CoverImageUrl, Description, DateCreated) VALUES('True Colors', '1986-10-14', 'https://tinyurl.com/st3bdr3h', 'True Colors is the second studio album by American singer Cyndi Lauper, released on September 16, 1986, by Portrait Records. The album spawned several commercially successful singles as "True Colors", "Change of Heart", and "What''s Going On" reached the top 20 of the Billboard Hot 100, with the first two charting within the top five. The album was produced by Lauper herself together with Lennie Petze.', DATE('now'));
